INSERT INTO `tintuc` (`ID`, `TieuDe`, `Metatitle`, `Anh`, `NoiDung`, `NgayDang`, `TrangThai`, `LoaiTin_ID`, `NhanVien_ID`) VALUES
(36, 'Giá heo hơi ngày 04/12', 'gia-heo-hoi-ngay-04/12', 'heo-2.jpg', '<p><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#333333\">Gi&aacute; heo hơi biến động nhẹ ở một số địa phương. Đ&aacute;ng ch&uacute; &yacute;, tại thủ phủ chăn nu&ocirc;i heo <a href=\"https://thanhnien.vn/dong-nai/\" target=\"_blank\" title=\"Đồng Nai\"><span style=\"color:#00739f\">Đồng Nai</span></a> gi&aacute; tiếp tục giảm 1.000 đồng xuống chỉ c&ograve;n 52.000 đồng/kg.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#111111\">Tại khu vực ph&iacute;a bắc, <a href=\"https://thanhnien.vn/gia-heo-hoi-hom-nay-2-12-2022-nguon-cung-du-thua-keo-gia-giam-post1527683.html\" title=\"Giá heo hơi hôm nay 2.12.2022: Nguồn cung dư thừa kéo giá giảm \"><span style=\"color:#00739f\">gi&aacute; heo hơi</span></a> phổ biến ở mức từ 53.000 - 54.000 đồng/kg. Hưng Y&ecirc;n v&agrave; Th&aacute;i B&igrave;nh tiếp tục duy tr&igrave; ở mức cao nhất với 55.000 đồng/kg. Ngược lại, c&aacute;c địa phương gồm Y&ecirc;n B&aacute;i, L&agrave;o Cai, Nam Định, H&agrave; Nam v&agrave; <a href=\"https://thanhnien.vn/ninh-binh/\" target=\"_blank\" title=\"Ninh Bình\"><span style=\"color:#00739f\">Ninh B&igrave;nh</span></a> đang thu mua heo hơi c&ugrave;ng mức 52.000 đồng/kg, thấp nhất khu vực.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#111111\">Ở miền Trung - T&acirc;y nguy&ecirc;n, gi&aacute; heo tại L&acirc;m Đồng tăng 1.000 đồng/kg l&ecirc;n mức 55.000 đồng/kg, cao nhất khu vực. Thấp hơn đến 5.000 đồng, gi&aacute; heo tại <a href=\"https://thanhnien.vn/binh-thuan/\" target=\"_blank\" title=\"Bình Thuận\"><span style=\"color:#00739f\">B&igrave;nh Thuận</span></a> chỉ với 50.000 đồng/kg, thấp nhất cả nước. Gi&aacute; heo hơi phổ biến trong khu vực n&agrave;y l&agrave; 52.000 - 54.000 đồng/kg.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#111111\">Tại <a href=\"https://thanhnien.vn/gia-heo-hoi-hom-nay-1-11-2022-giam-gia-tren-dien-rong-post1516605.html\" title=\"Giá heo hơi hôm nay 1.11.2022: Giảm giá trên diện rộng\"><span style=\"color:#00739f\">thủ phủ chăn nu&ocirc;i</span></a> Đồng Nai h&ocirc;m nay giảm 1.000 đồng/kg về mức 52.000 đồng/kg. Ở khu vực Đ&ocirc;ng Nam bộ, mức gi&aacute; 52.000 đồng/kg c&ograve;n ghi nhận tại B&agrave; Rịa-Vũng T&agrave;u. C&aacute;c địa phương c&ograve;n lại c&oacute; gi&aacute; heo l&agrave; 53.000 đồng/kg.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\"><span style=\"font-size:13.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#111111\">Ở <a href=\"https://thanhnien.vn/lam-gi-de-nguoi-dan-dbscl-thoat-ngheo-post1527793.html\" title=\"Làm gì để người dân ĐBSCL thoát nghèo?\"><span style=\"color:#00739f\">ĐBSCL</span></a> gi&aacute; heo giảm 1.000 đồng tại Bến Tre, về mức 52.000 đồng/kg. Gi&aacute; heo hơi ở khu vực n&agrave;y hiện 52.000 - 53.000 đồng/kg. <a href=\"https://thanhnien.vn/kien-giang/\" target=\"_blank\" title=\"Kiên Giang\"><span style=\"color:#00739f\">Ki&ecirc;n Giang</span></a> vẫn l&agrave; địa phương c&oacute; gi&aacute; heo hơi thấp nhất khu vực với 51.000 đồng/kg.</span></span></span></span></span></p>', '2022-12-04 12:17:55', 1, 3, NULL),
(38, 'BỆNH VIÊM PHỔI DO MYCOPLASMA Ở HEO', 'benh-viem-phoi-do-mycoplasma-o-heo', 'heo-7.jpg', '<p><strong>Nguy&ecirc;n nh&acirc;n</strong></p>\r\n\r\n<p>Do&nbsp;<em>Mycoplasma hyopneumoniae g&acirc;y ra.</em></p>\r\n\r\n<p><strong>Đường truyền bệnh</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Bệnh truyền l&acirc;y qua đường h&ocirc; hấp. Vi khuẩn c&oacute; sẵn trong đường h&ocirc; hấp, khi gặp điều kiện bất lợi sẽ g&acirc;y bệnh.</p>\r\n\r\n<p>- Do tiếp x&uacute;c trực tiếp giữa heo bệnh v&agrave; heo khỏe.</p>\r\n\r\n<p>- Tiếp x&uacute;c giữa heo mẹ (c&oacute; mang mầm bệnh) v&agrave; heo con. Khi trong đ&agrave;n c&oacute; heo mắc bệnh, sau cơn ho c&aacute;c chất tiết với nhiều hạt nhỏ, lơ lửng trong kh&ocirc;ng kh&iacute;, heo khỏe mạnh h&iacute;t phải sẽ mắc bệnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Triệu chứng.</strong></p>\r\n\r\n<p>- Đặc điểm của bệnh l&agrave; heo ho khan k&eacute;o d&agrave;i, nhất l&agrave; sau khi vận động, bị rượt đuổi, ho v&agrave;o ban đ&ecirc;m, l&uacute;c s&aacute;ng sớm.</p>\r\n\r\n<p>- Heo giảm ăn, chậm lớn, sức kh&aacute;ng bệnh yếu. V&igrave; thế rất dễ kết hợp với c&aacute;c vi tr&ugrave;ng g&acirc;y vi&ecirc;m phổi kh&aacute;c sẽ tạo n&ecirc;n t&igrave;nh trạng vi&ecirc;m phổi nặng với triệu chứng sốt cao, ho nhiều, kh&oacute; thở.&nbsp;</p>\r\n\r\n<p><strong>Điều trị</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Bệnh c&oacute; thể điều trị bằng c&aacute;c loại kh&aacute;ng sinh thuộc nh&oacute;m Quinolones, Tetracycline hoặc Macrolides.</p>\r\n\r\n<p>- Khi trong đ&agrave;n heo c&oacute; triệu chứng ho nhiều cần trộn một trong c&aacute;c loại thuốc sau đ&acirc;y v&agrave;o thức ăn, cho heo ăn li&ecirc;n tục từ 7-10 ng&agrave;y:&nbsp;<strong>BIO-TYLAN FORT</strong>,&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-speclin.html\" target=\"_blank\"><strong>BIO-SPECLIN</strong></a>,&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-cam-thuy-cam/bio-anti-mycoplasma.html\" target=\"_blank\"><strong>BIO ANTI-MYCOPLASMA &reg;</strong></a>.</p>\r\n\r\n<p>- Tr&ecirc;n c&aacute;c heo c&oacute; triệu chứng ho nhiều, sốt cao, kh&oacute; thở n&ecirc;n t&aacute;ch ra khỏi đ&agrave;n v&agrave; tiến h&agrave;nh điều trị bằng c&aacute;ch ti&ecirc;m một trong c&aacute;c loại thuốc sau:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-anflox-100.html\" target=\"_blank\"><strong>BIO-ANFLOX 100</strong></a>&nbsp;+&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-bromhexine.html\" target=\"_blank\"><strong>BIO-BROMHEXINE.</strong></a></li>\r\n	<li><strong>BIO COLI MUTIN FORT.</strong></li>\r\n	<li><strong>BIO SPIRA-COLISTIN.</strong></li>\r\n	<li><a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-cam-thuy-cam/bio-marcosone.html\" target=\"_blank\"><strong>BIO-MARCOSONE &reg;</strong></a>&nbsp;+&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-bromhexine.html\" target=\"_blank\"><strong>BIO-BROMHEXINE</strong></a>.</li>\r\n	<li>Thuốc kh&aacute;ng vi&ecirc;m:&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-dexa.html\" target=\"_blank\"><strong>BIO-DEXA &reg;</strong></a>, Thuốc hạ sốt:&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-anazin-c.html\" target=\"_blank\"><strong>BIO-ANAZIN.C</strong></a>.</li>\r\n	<li>Để tăng sức đề kh&aacute;ng cho heo n&ecirc;n cấp th&ecirc;m&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-cevit.html\" target=\"_blank\"><strong>BIO-CEVIT</strong></a>&nbsp;v&agrave;&nbsp;<a href=\"http://biopharmachemie.com/san-pham/san-pham-cho-gia-suc/bio-b-complex.html\" target=\"_blank\"><strong>BIO-B.COMPLEX &reg;</strong></a>&nbsp;.</li>\r\n	<li>Lịch tr&igrave;nh điều trị trung b&igrave;nh từ 5 - 7 ng&agrave;y.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ph&ograve;ng bệnh</strong></p>\r\n\r\n<p>- Ph&ograve;ng bệnh bằng biện ph&aacute;p quản l&yacute;:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Chuồng trại th&ocirc;ng tho&aacute;ng, sạch sẽ.</li>\r\n	<li>S&aacute;t tr&ugrave;ng chuồng, m&aacute;ng ăn, m&aacute;ng uống thật kỷ lưỡng.</li>\r\n	<li>Hạn chế tắm heo, chỉ tắm trong trường hợp trời nắng n&oacute;ng.</li>\r\n	<li>Kh&ocirc;ng nu&ocirc;i nhốt với mật độ cao. Kh&ocirc;ng nu&ocirc;i heo ở c&aacute;c lứa tuổi kh&aacute;c nhau trong c&ugrave;ng một &ocirc; chuồng.</li>\r\n	<li>&Aacute;p dụng biện ph&aacute;p c&ugrave;ng v&agrave;o c&ugrave;ng ra.</li>\r\n	<li>Trường hợp sử dụng thức ăn bột, n&ecirc;n l&agrave;m ẩm trước khi cho ăn để tr&aacute;nh bụi.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Ph&ograve;ng bệnh bằng thuốc:</p>\r\n\r\n<ul>\r\n	<li>Trộn kh&aacute;ng sinh v&agrave;o thức ăn heo thịt trong 4 -5 ng&agrave;y, định k&yacute; 3 - 4 tuần 1 đợt.</li>\r\n	<li>Đặc biệt trộn kh&aacute;ng sinh v&agrave;o thức ăn heo n&aacute;i trước khi sanh 2 tuần trong 4 -5 ng&agrave;y nhằm hạn chế sự l&acirc;y lan Mycoplasma từ n&aacute;i sang con. Ti&ecirc;m ph&ograve;ng vaccin Mycoplasma cho heo con một mũi duy nhất v&agrave;o l&uacute;c 21 ng&agrave;y tuổi v&agrave; c&oacute; thể bảo hộ đến khi xuất chuồng (chỉ ti&ecirc;m ngừa cho c&aacute;c heo con khỏe mạnh).&nbsp;</li>\r\n</ul>', '2022-12-04 22:05:43', 1, 2, NULL);

